 WITH horarios_agrupados AS (
         SELECT horarios.id AS horario,
            horarios.codigo_disciplina AS disciplina,
            horarios.turma,
            (horarios.id - row_number() OVER (PARTITION BY horarios.codigo_disciplina, horarios.turma ORDER BY horarios.id)) AS grupo
